Led.sh #!/bin/bash # File organizer script using fd # Organizes files from SRC_DIR into categorized subdirectories in DEST_BASE # Creates unique timestamped logs (text and JSON) for each run to avoid interference # Skip_Dir contains relative paths (e.g., Android/data) to exclude from SRC_DIR # Handles duplicate files by renaming with numbered suffixes (e.g., file(1).mkv, file(2).mkv) # Configuration SRC_DIR=&#34;/storage/emulated/0&#34; DEST_BASE=&#34;/storage/emulated/0/00/sort/&#34; LOG_DIR=&#34;/storage/emulated/0/0l&#34; PHOTO_DIR=&#34;$DEST_BASE/pho&#34; VIDEO_DIR=&#34;$DEST_BASE/vid&#34; APK_DIR=&#34;$DEST_BASE/apk&#34; AUDIO_DIR=&#34;$DEST_BASE/mus&#34; DOC_DIR=&#34;$DEST_BASE/doc&#34; # Size filters (configurable) MIN_SIZE=&#34;+10k&#34; # Minimum file size (e.g., 10KB) MAX_SIZE=&#34;-5G&#34; # Maximum file size (e.g., 5GB) # Directories to skip (relative to SRC_DIR) Skip_Dir=( &#34;Android&#34; &#34;arch&#34; &#34;00&#34; &#34;Ringtones&#34; &#34;0b&#34; &#34;00/sort&#34; # Exclude the destination base directory ) # Extensions to skip (e.g., system or temporary files) Skip_Extensions=( &#34;nomedia&#34; &#34;cache&#34; &#34;rd&#34; &#34;tmp&#34; &#34;bak&#34; &#34;log&#34; ) # File type definitions declare -A FILE_TYPES=( [&#34;jpg&#34;]=&#34;pho&#34; [&#34;jpeg&#34;]=&#34;pho&#34; [&#34;png&#34;]=&#34;pho&#34; [&#34;gif&#34;]=&#34;pho&#34; [&#34;bmp&#34;]=&#34;pho&#34; [&#34;webp&#34;]=&#34;pho&#34; [&#34;heic&#34;]=&#34;pho&#34; [&#34;tiff&#34;]=&#34;pho&#34; [&#34;mp4&#34;]=&#34;vid&#34; [&#34;mkv&#34;]=&#34;vid&#34; [&#34;mov&#34;]=&#34;vid&#34; [&#34;avi&#34;]=&#34;vid&#34; [&#34;flv&#34;]=&#34;vid&#34; [&#34;webm&#34;]=&#34;vid&#34; [&#34;wmv&#34;]=&#34;vid&#34; [&#34;3gp&#34;]=&#34;vid&#34; [&#34;apk&#34;]=&#34;apk&#34; [&#34;apkm&#34;]=&#34;apk&#34; [&#34;xapk&#34;]=&#34;apk&#34; [&#34;mp3&#34;]=&#34;mus&#34; [&#34;wav&#34;]=&#34;mus&#34; [&#34;flac&#34;]=&#34;mus&#34; [&#34;ogg&#34;]=&#34;mus&#34; [&#34;opus&#34;]=&#34;mus&#34; [&#34;m4a&#34;]=&#34;mus&#34; [&#34;aac&#34;]=&#34;mus&#34; [&#34;wma&#34;]=&#34;mus&#34; [&#34;html&#34;]=&#34;doc&#34; [&#34;pdf&#34;]=&#34;doc&#34; [&#34;doc&#34;]=&#34;doc&#34; [&#34;docx&#34;]=&#34;doc&#34; [&#34;epub&#34;]=&#34;doc&#34; [&#34;odt&#34;]=&#34;doc&#34; [&#34;rtf&#34;]=&#34;doc&#34; [&#34;pages&#34;]=&#34;doc&#34; [&#34;zip&#34;]=&#34;doc&#34; [&#34;rar&#34;]=&#34;doc&#34; [&#34;7z&#34;]=&#34;doc&#34; [&#34;tar&#34;]=&#34;doc&#34; [&#34;tar.gz&#34;]=&#34;doc&#34; [&#34;tar.bz2&#34;]=&#34;doc&#34; [&#34;tar.xz&#34;]=&#34;doc&#34; ) # Check dependencies if ! command -v fd &gt;/dev/null 2&gt;&amp;1; then echo &#34;Error: fd is not installed. Please install it first.&#34; exit 1 fi if ! command -v jq &gt;/dev/null 2&gt;&amp;1; then echo &#34;Error: jq is not installed. Please install it for JSON logging.&#34; exit 1 fi # Validate SRC_DIR if [[ ! -d &#34;$SRC_DIR&#34; || ! -r &#34;$SRC_DIR&#34; ]]; then echo &#34;Error: Source directory &#39;$SRC_DIR&#39; does not exist or is not readable.&#34; exit 1 fi # Create directories mkdir -p &#34;$PHOTO_DIR&#34; &#34;$VIDEO_DIR&#34; &#34;$APK_DIR&#34; &#34;$AUDIO_DIR&#34; &#34;$DOC_DIR&#34; &#34;$LOG_DIR&#34; || { echo &#34;Error: Failed to create directories. Check permissions.&#34; exit 1 } # Initialize logs with unique timestamp TIMESTAMP=$(date +%Y%m%d_%H%M%S) LOG_FILE=&#34;$LOG_DIR/organize_${TIMESTAMP}.log&#34; JSON_LOG=&#34;$LOG_DIR/organize_${TIMESTAMP}.json&#34; SKIPPED_LOG=&#34;$LOG_DIR/skipped_${TIMESTAMP}.log&#34; # Initialize logs echo &#39;{&#34;moved&#34;: [], &#34;skipped&#34;: [], &#34;errors&#34;: []}&#39; &gt; &#34;$JSON_LOG&#34; touch &#34;$LOG_FILE&#34; &#34;$SKIPPED_LOG&#34; # Cleanup temporary files on exit trap &#39;rm -f &#34;${JSON_LOG}.tmp.moved&#34; &#34;${JSON_LOG}.tmp.skipped&#34; &#34;${JSON_LOG}.tmp.errors&#34; &#34;${JSON_LOG}.tmp.array&#34; &#34;${JSON_LOG}.tmp2&#34;&#39; EXIT INT TERM # Build fd command as an array FD_CMD=(fd -t f -0 &#34;$SRC_DIR&#34;) for ext in &#34;${!FILE_TYPES[@]}&#34;; do FD_CMD+=(-e &#34;$ext&#34;) done FD_CMD+=(--size &#34;$MIN_SIZE&#34; --size &#34;$MAX_SIZE&#34;) for dir in &#34;${Skip_Dir[@]}&#34;; do FD_CMD+=(--exclude &#34;${dir}/**&#34;) done # Initialize counters MOVED=0 SKIPPED=0 ERRORS=0 # Function to generate a unique destination path get_unique_dest() { local dest_dir=&#34;$1&#34; local filename=&#34;$2&#34; local base_name=&#34;${filename%.*}&#34; local extension=&#34;${filename##*.}&#34; local dest_file=&#34;$dest_dir/$filename&#34; local counter=1 # If file exists, append numbered suffix while [ -f &#34;$dest_file&#34; ]; do dest_file=&#34;$dest_dir/${base_name}($counter).${extension}&#34; ((counter++)) done echo &#34;$dest_file&#34; } # Function to get effective extension get_effective_extension() { local filename=&#34;$1&#34; local simple_ext=&#34;${filename##*.}&#34; simple_ext=&#34;${simple_ext,,}&#34; # Convert to lowercase local effective_ext=&#34;$simple_ext&#34; # Handle multi-part extensions case &#34;$simple_ext&#34; in &#34;gz&#34;|&#34;bz2&#34;|&#34;xz&#34;) local before=&#34;${filename%.$simple_ext}&#34; local before_ext=&#34;${before##*.}&#34; before_ext=&#34;${before_ext,,}&#34; if [[ &#34;$before_ext&#34; == &#34;tar&#34; ]]; then effective_ext=&#34;tar.$simple_ext&#34; fi ;; esac echo &#34;$effective_ext&#34; } # Process files function process_file() { local file=&#34;$1&#34; local filename=$(basename &#34;$file&#34;) local extension extension=$(get_effective_extension &#34;$filename&#34;) local simple_ext=&#34;${filename##*.}&#34; simple_ext=&#34;${simple_ext,,}&#34; # Skip files with no extension if [[ -z &#34;$simple_ext&#34; || &#34;$simple_ext&#34; =~ [^a-z0-9] ]]; then echo &#34;$(date): SKIPPED (no valid extension) - $file&#34; &gt;&gt; &#34;$LOG_FILE&#34; echo &#34;$file -&gt; no valid extension&#34; &gt;&gt; &#34;$SKIPPED_LOG&#34; jq -n --arg src &#34;$file&#34; --arg reason &#34;no valid extension&#34; &#39;{source:$src, reason:$reason}&#39; &gt;&gt; &#34;${JSON_LOG}.tmp.skipped&#34; ((SKIPPED++)) return fi echo &#34;Processing file: $file&#34; &gt;&gt; &#34;$LOG_FILE&#34; # Check if simple extension is in skip list for skip_ext in &#34;${Skip_Extensions[@]}&#34;; do if [[ &#34;$simple_ext&#34; == &#34;${skip_ext,,}&#34; ]]; then echo &#34;$(date): SKIPPED (extension in skip list: $simple_ext) - $file&#34; &gt;&gt; &#34;$LOG_FILE&#34; echo &#34;$file -&gt; skipped extension: $simple_ext&#34; &gt;&gt; &#34;$SKIPPED_LOG&#34; jq -n --arg src &#34;$file&#34; --arg reason &#34;skipped extension: $simple_ext&#34; &#39;{source:$src, reason:$reason}&#39; &gt;&gt; &#34;${JSON_LOG}.tmp.skipped&#34; ((SKIPPED++)) return fi done # Check if effective extension is defined in FILE_TYPES if [[ -n &#34;${FILE_TYPES[$extension]}&#34; ]]; then local dest_dir case &#34;${FILE_TYPES[$extension]}&#34; in pho) dest_dir=&#34;$PHOTO_DIR&#34; ;; vid) dest_dir=&#34;$VIDEO_DIR&#34; ;; apk) dest_dir=&#34;$APK_DIR&#34; ;; mus) dest_dir=&#34;$AUDIO_DIR&#34; ;; doc) dest_dir=&#34;$DOC_DIR&#34; ;; esac local dest_file dest_file=$(get_unique_dest &#34;$dest_dir&#34; &#34;$filename&#34;) if mv &#34;$file&#34; &#34;$dest_file&#34; 2&gt;&gt;&#34;$LOG_FILE&#34;; then echo &#34;$(date): MOVED - $file -&gt; $dest_file&#34; &gt;&gt; &#34;$LOG_FILE&#34; jq -n --arg src &#34;$file&#34; --arg dst &#34;$dest_file&#34; &#39;{source:$src, destination:$dst, method:&#34;move&#34;}&#39; &gt;&gt; &#34;${JSON_LOG}.tmp.moved&#34; ((MOVED++)) else if cp &#34;$file&#34; &#34;$dest_file&#34; 2&gt;&gt;&#34;$LOG_FILE&#34; &amp;&amp; rm &#34;$file&#34; 2&gt;&gt;&#34;$LOG_FILE&#34;; then echo &#34;$(date): COPIED+REMOVED (cross-device) - $file -&gt; $dest_file&#34; &gt;&gt; &#34;$LOG_FILE&#34; jq -n --arg src &#34;$file&#34; --arg dst &#34;$dest_file&#34; &#39;{source:$src, destination:$dst, method:&#34;copy+remove&#34;}&#39; &gt;&gt; &#34;${JSON_LOG}.tmp.moved&#34; ((MOVED++)) else local error_msg=&#34;Failed to move/copy: $?&#34; echo &#34;$(date): ERROR - $error_msg - $file&#34; &gt;&gt; &#34;$LOG_FILE&#34; jq -n --arg src &#34;$file&#34; --arg dst &#34;$dest_file&#34; --arg err &#34;$error_msg&#34; &#39;{source:$src, destination:$dst, error:$err}&#39; &gt;&gt; &#34;${JSON_LOG}.tmp.errors&#34; ((ERRORS++)) fi fi else echo &#34;$(date): SKIPPED (unknown extension: $extension) - $file&#34; &gt;&gt; &#34;$LOG_FILE&#34; echo &#34;$file -&gt; unknown extension: $extension&#34; &gt;&gt; &#34;$SKIPPED_LOG&#34; jq -n --arg src &#34;$file&#34; --arg reason &#34;unknown extension: $extension&#34; &#39;{source:$src, reason:$reason}&#39; &gt;&gt; &#34;${JSON_LOG}.tmp.skipped&#34; ((SKIPPED++)) fi } # Process files using null-delimited input while IFS= read -r -d &#39;&#39; file; do process_file &#34;$file&#34; done &lt; &lt;(&#34;${FD_CMD[@]}&#34;) # Write batched JSON updates for type in moved skipped errors; do if [ -f &#34;${JSON_LOG}.tmp.$type&#34; ]; then jq -s &#39;.&#39; &#34;${JSON_LOG}.tmp.$type&#34; &gt; &#34;${JSON_LOG}.tmp.array&#34; jq --argjson array &#34;$(cat &#34;${JSON_LOG}.tmp.array&#34;)&#34; &#34;.$type = \$array&#34; &#34;$JSON_LOG&#34; &gt; &#34;${JSON_LOG}.tmp2&#34; mv &#34;${JSON_LOG}.tmp2&#34; &#34;$JSON_LOG&#34; rm -f &#34;${JSON_LOG}.tmp.$type&#34; &#34;${JSON_LOG}.tmp.array&#34; fi done # Finalize echo -e &#34;\nFile organization complete! Moved $MOVED files, skipped $SKIPPED files, errors $ERRORS.&#34; echo &#34;Logs saved to: $LOG_DIR&#34; echo &#34;JSON log: $JSON_LOG&#34;
